mapper.update:
   	UPDATE product
   	
   		prod_name 		= #{prodName} ,
		prod_detail		= #{prodDetail:VARCHAR},
		manufacture_day = #{manuDate:VARCHAR} ,
		price			= #{price:VARCHAR}
   	
   	WHERE prod_no = #{prodNo}

mapper.select:

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT  p.prod_no, p.prod_name , p.price , p.category, p.reg_date, t.tran_status_code
										FROM product p, transaction t
										
											
												p.PROD_NO = t.PROD_NO(+) 
												
									 				AND p.prod_no = #{searchKeyword}
												
												
									 				AND p.prod_name = #{searchKeyword}
												
												
									 				AND p.price = #{searchKeyword}
												
												
									 				AND p.category = #{searchKeyword}
												
											
										
										ORDER BY p.prod_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*) 
  	FROM(	SELECT prod_name , price , manufacture_day
					FROM product
					
						
							
					 			prod_no = #{searchKeyword}
							
							
					 			prod_name = #{searchKeyword}
							
							
								price = #{searchKeyword}
							
						
					 ) countTable
